    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
String rollNo = jTextField1.getText();
String name = jTextField2.getText();
String attendance1 = jTextField3.getText();
String attendance2 = jTextField4.getText();
String attendance3 = jTextField5.getText();
String attendance4 = jTextField6.getText();
String attendance5 = jTextField7.getText();
String attendance6 = jTextField8.getText();
String attendance7 = jTextField9.getText();
String attendance8 = jTextField10.getText();

try {
    Connection con = ConnectionProvider.getcon();
    
    // Prepare the SQL update statement
    PreparedStatement ps = con.prepareStatement(
        "UPDATE attendance SET name=?, 22MDC31=?, 22MDC32=?, 22MDC33=?, 22MDC34=?, 22MDC35=?, 22MDC36=?, 22MDC37=?, 22MDC38=? WHERE RegisterNumber=?"
    );
    
    // Set parameters
    ps.setString(1, name);
    ps.setString(2, attendance1);
    ps.setString(3, attendance2);
    ps.setString(4, attendance3);
    ps.setString(5, attendance4);
    ps.setString(6, attendance5);
    ps.setString(7, attendance6);
    ps.setString(8, attendance7);
    ps.setString(9, attendance8);
    ps.setString(10, rollNo); // Last parameter is rollNo

    // Execute the update
    int rowsUpdated = ps.executeUpdate();
    if (rowsUpdated > 0) {
        JOptionPane.showMessageDialog(null, "Successfully updated");
    } else {
        JOptionPane.showMessageDialog(null, "No records were updated.");
    }
    
    setVisible(false);
    new Updateattend().setVisible(true);
} catch (Exception e) {
    JOptionPane.showMessageDialog(null, e.getMessage());
}

    }//GEN-LAST:event_jButton1ActionPerformed

