    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
String rollNo = jTextField1.getText();
try {
    Connection con = ConnectionProvider.getcon();
    Statement st = con.createStatement();
    
    // Corrected SQL query: Added '*' to select all columns
    ResultSet rs = st.executeQuery("SELECT * FROM attendance WHERE RegisterNumber ='" + rollNo + "'");

    if (rs.next()) {
        // Assuming columns start from index 2 for the first data field
        jTextField2.setText(rs.getString(2));
        jTextField3.setText(rs.getString(3));
        jTextField4.setText(rs.getString(4));
        jTextField5.setText(rs.getString(5));
        jTextField6.setText(rs.getString(6));
        jTextField7.setText(rs.getString(7));
        jTextField8.setText(rs.getString(8));
        jTextField9.setText(rs.getString(9));
        jTextField10.setText(rs.getString(10));
        jTextField1.setEditable(false);
    } else {
        // Inform the user if the Register Number does not exist
        JOptionPane.showMessageDialog(null, "Register number does not exist.");
    }

} catch (Exception e) {
    // Show the exception message
    JOptionPane.showMessageDialog(null, e.getMessage());
} 

    }//GEN-LAST:event_jButton4ActionPerformed

